 val formatter = DateTimeFormatter.ofPattern("M/d")
 createdAt.format(formatter)
 }
 }

 // 1시간 이상일 경우
 if (duration.toHours() >= 1) {
 return "${duration.toHours()}시간"
 }

 // 1분 이상일 경우
 if (duration.toMinutes() >= 1) {
 return "${duration.toMinutes()}분"
