 BACKGROUND_CHANNEL_ID,
 BACKGROUND_CHANNEL,
 NotificationManager.IMPORTANCE_DEFAULT
 ).apply {
 description = BACKGROUND_CHANNEL_DESCRIPTION
 }

 val manager = getSystemService(NotificationManager::class.java)
