 is SettingEvent.NavigateBack -> navigateBack()
 is SettingEvent.Logout -> navigateToLogin()
 is SettingEvent.UnregisterUserSuccess -> navigateToLogin()
 is SettingEvent.UnregisterUserFailure -> {
