```kotlin:C:/Users/mj010/.gradle/caches/modules-2/files-2.1/org.jetbrains.kotlinx/kotlinx-coroutines-core-jvm/1.10.1/acdf84accbb0635d4c0263b987ddf9a79cb6ea77/kotlinx-coroutines-core-jvm-1.10.1-sources.jar!/commonMain/Delay.kt, lines=120-129
public suspend fun delay(timeMillis: Long) {
 if (timeMillis <= 0) return // don't delay
 return suspendCancellableCoroutine sc@ { cont: CancellableContinuation<Unit> ->
 // if timeMillis == Long.MAX_VALUE then just wait forever like awaitCancellation, don't schedule.
 if (timeMillis < Long.MAX_VALUE) {
 cont.context.delay.scheduleResumeAfterDelay(timeMillis, cont)
 }
 }
}

