 enterTransition = {
 defaultSlideFadeIn()
 },
 exitTransition = {
 defaultSlideFadeOut()
 },
 ) {
 SearchRoute(
 navigateBack = navigateBack,
 navigateToPost = navigateToPost,
 )
 }

