 enterTransition = {
 defaultSlideUpFadeIn()
 },
 exitTransition = {
 defaultSlideDownFadeOut()
 },
 ) {
 WritePostRoute(
 navigateToPost = navigateToPostReplacing,
 navigateBack = navigateBack
 )
 }

