 enterTransition = {
 defaultSlideFadeIn()
 },
 exitTransition = {
 defaultSlideFadeOut()
 },
 ) {
 PostRoute(
 navigateBack = navigateBack,
 navigateToUpdatePost = navigateToUpdatePost
 )
 }

